import React, { useState, useEffect } from 'react'
import { connect } from 'redux-bundler-react'
import BaseScreen from './BaseScreen'
import { Button } from 'semantic-ui-react'
import Table from '../table/Table'
import './ThreadScreen.css'


interface IThreadsScreen {
  threadsActive: any
  doThreadsCreate: () => any
  doCollectionsCreate: (name: string) => any
}

const ThreadScreen = ({ threadsActive, doThreadsCreate, doCollectionsCreate }: IThreadsScreen) => {
  const [name, setName] = useState<string>('')


  useEffect(() => {
    const setup = async () => {
      setName(threadsActive[0].name)
      if (!threadsActive) {
        doThreadsCreate()
      }
    }
    setup()
  }, [])

  const selectExisting = (name: string) => {
    setName(name)
  }
  return (
    <BaseScreen>
      <div className='optionsRow'>
        {threadsActive && threadsActive.map((collection: any, index: number) => (
          <div
            key={index}
            className='tableButton'
            onClick={() => selectExisting(collection.name)}
          >
            {collection.name}
          </div>
        ))}
        <div className="inputContainer">
          <input className='nameInput' placeholder='table name' type='text' value={name} onChange={e => setName(e.target.value)}/>
          <Button className='ui primary button' onClick={() => doCollectionsCreate(name)}>Create</Button>
        </div>
      </div>
      {threadsActive && (
        <Table name={name}/>
      )}
    </BaseScreen>
  )
}

export default connect(
  'doThreadsCreate',
  'doCollectionsCreate',
  'selectThreadsActive',
  ThreadScreen
)
