// @ts-nocheck
import React, { useMemo, useEffect, useState } from 'react'
import { useTable, usePagination } from 'react-table'
import { Where } from '@textile/hub'
import './Table.css'

const Table = ({ threadId, client }) => {
  const columns = useMemo(
    () => [
      {
        Header: 'Astronauts',
        columns: [
          {
            Header: 'Name',
            accessor: 'name',
          },
          {
            Header: 'Missions',
            accessor: 'missions',
          },
        ],
      },
    ],
    []
  )
  const rowObject = { _id: Math.random().toString(36), name: "", missions: 0 }
  const [data, setData] = useState([
    { _id: Math.random().toString(36), name: "Nanssar", missions: 6 },
    { _id: Math.random().toString(36), name: "Fred", missions: 6 }
  ])
  const [skipPageReset, setSkipPageReset] = useState(false)

  useEffect(() => {
    if (client && threadId) {
      createCollection()
      console.log(data, 'DATA')
    }
  }, [client, threadId])

  useEffect(() => {
    saveCollectionUpdates()
  }, [data])

  const createCollection = async () => {
    const schema = {
      $schema: 'http://json-schema.org/draft-07/schema#',
      title: 'Astronaut',
      type: 'object',
      properties: {
        _id: { type: 'string' },
        name: { type: 'string' },
        missions: {
          type: 'number',
          minimum: 0,
        },
      },
    }
    await client.newCollection(threadId, 'Astronaut', schema)
    await client.create(threadId, 'Astronaut', data)
  }

  // When our cell renderer calls updateMyData, we'll use
  // the rowIndex, columnId and new value to update the
  // original data
  const updateMyData = async (rowIndex, columnId, value) => {
    // We also turn on the flag to not reset the page
    setSkipPageReset(true)
    console.log(rowIndex, columnId, value, 'HOOOO')
    setData(old =>
      old.map((row, index) => {
        if (index === rowIndex) {
          return {
            ...old[rowIndex],
            [columnId]: value,
          }
        }
        return row
      })
    )
  }

  const saveCollectionUpdates = async () => {
    if (client && threadId) {
      console.log('newDATA', data)
      await client.save(threadId, 'Astronaut', data)

      const query = new Where('missions').eq(6)
      const list = await client.find(threadId, 'Astronaut', query)
      console.log('list', list)
    }
  }

  const addRow = async () => {
    setData([...data, rowObject])
    await client.create(threadId, 'Astronaut', rowObject)
  }

  const EditableCell = ({
    value: initialValue,
    row: { index },
    column: { id },
    updateMyData, // This is a custom function that we supplied to our table instance
  }) => {
    // We need to keep and update the state of the cell normally
    const [value, setValue] = React.useState(initialValue)

    const onChange = e => {
      setValue(e.target.value)
    }

    // We'll only update the external data when the input is blurred
    const onBlur = () => {
      updateMyData(index, id, value)
    }

    // If the initialValue is changed external, sync it up with our state
    useEffect(() => {
      setValue(initialValue)
    }, [initialValue])

    return (<input value={value} onChange={onChange} onBlur={onBlur} />)
  }

  // Set our editable cell renderer as the default Cell renderer
  const defaultColumn = {
    Cell: EditableCell,
  }

  // For this example, we're using pagination to illustrate how to stop
  // the current page from resetting when our data changes
  // Otherwise, nothing is different here.
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    prepareRow,
    page,
    canPreviousPage,
    canNextPage,
    pageOptions,
    pageCount,
    gotoPage,
    nextPage,
    previousPage,
    setPageSize,
    state: { pageIndex, pageSize },
  } = useTable(
    {
      columns,
      data,
      defaultColumn,
      // use the skipPageReset option to disable page resetting temporarily
      autoResetPage: !skipPageReset,
      // updateMyData isn't part of the API, but
      // anything we put into these options will
      // automatically be available on the instance.
      // That way we can call this function from our
      // cell renderer!
      updateMyData,
    },
    usePagination
  )

  // After data chagnes, we turn the flag back off
  // so that if data actually changes when we're not
  // editing it, the page is reset
  useEffect(() => {
    setSkipPageReset(false)
  }, [data])

  return (
    <>
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map(headerGroup => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map(column => (
                <th {...column.getHeaderProps()}>{column.render('Header')}</th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {page.map((row, i) => {
            prepareRow(row)
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map(cell => {
                  return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                })}
              </tr>
            )
          })}
        </tbody>
      </table>
      <button onClick={addRow}>
        add row
      </button>
      <div className="pagination">
        <button onClick={() => gotoPage(0)} disabled={!canPreviousPage}>
          {'<<'}
        </button>{' '}
        <button onClick={() => previousPage()} disabled={!canPreviousPage}>
          {'<'}
        </button>{' '}
        <button onClick={() => nextPage()} disabled={!canNextPage}>
          {'>'}
        </button>{' '}
        <button onClick={() => gotoPage(pageCount - 1)} disabled={!canNextPage}>
          {'>>'}
        </button>{' '}
        <span>
          Page{' '}
          <strong>
            {pageIndex + 1} of {pageOptions.length}
          </strong>{' '}
        </span>
        <span>
          | Go to page:{' '}
          <input
            type="number"
            defaultValue={pageIndex + 1}
            onChange={e => {
              const page = e.target.value ? Number(e.target.value) - 1 : 0
              gotoPage(page)
            }}
            style={{ width: '100px' }}
          />
        </span>{' '}
        <select
          value={pageSize}
          onChange={e => {
            setPageSize(Number(e.target.value))
          }}
        >
          {[10, 20, 30, 40, 50].map(pageSize => (
            <option key={pageSize} value={pageSize}>
              Show {pageSize}
            </option>
          ))}
        </select>
      </div>
    </>
  )
}

export default Table
