import React from 'react'
import Table from './Table'

import "semantic-ui-css/semantic.min.css"
import { Buckets, KeyInfo, PrivateKey, Client, ThreadID, Identity } from '@textile/hub'
import { Button, Header, Segment } from "semantic-ui-react"

import './App.css'

interface AppState {
  client?: Client
  threadId?: ThreadID
  isLoading: boolean
  identity?: Identity
  buckets?: Buckets
  bucketKey?: string
  www?: string
  url?: string
  ipns?: string
}

class App extends React.Component {
  keyInfo: KeyInfo = {
    key: 'bgdhubuxfallvfnvzeiaxip4kee',
  }
  state: AppState = {
    isLoading: false,
  }
  async componentDidMount() {
    // Clear your user during development
    // await localStorage.clear()
    const identity = await this.getIdentity()
    // you might want to do the I18N setup here
    this.setState({
      identity: identity
    })

    // get their photo bucket
    const {bucketKey, buckets} = await this.getBucketKey()
    this.setState({
      buckets: buckets,
      bucketKey: bucketKey
    })

    await this.getBucketLinks()

    const client = await this.getClientInfo()
    const threadId = await this.getDatabase(client)
    this.setState({
      client: client,
      threadId: threadId
    })
  }

  /**
   * getIdentity uses a basic private key identity.
   * The user's identity will be cached client side. This is long
   * but ephemeral storage not sufficient for production apps.
   *
   * Read more here:
   * https://docs.textile.io/tutorials/hub/libp2p-identities/
   */
  getIdentity = async (): Promise<PrivateKey> => {
    try {
      var storedIdent = localStorage.getItem("identity")
      if (storedIdent === null) {
        throw new Error('No identity')
      }
      const restored = PrivateKey.fromString(storedIdent)
      return restored
    }
    catch (e) {
      /**
       * If any error, create a new identity.
       */
      try {
        const identity = await PrivateKey.fromRandom()
        const identityString = identity.toString()
        localStorage.setItem("identity", identityString)
        return identity
      } catch (err) {
        return err.message
      }
    }
  }

  /**
   * getBucketKey will create a new Buckets client with the UserAuth
   * and then open our custom bucket named, 'io.textile.dropzone'
   */
  getBucketKey = async () => {
    if (!this.state.identity) {
      throw new Error('Identity not set')
    }
    const buckets = await Buckets.withKeyInfo(this.keyInfo)
    // Authorize the user and your insecure keys with getToken
    await buckets.getToken(this.state.identity)
    // console.log(buckets, this.state.identity)
    const buck = await buckets.getOrCreate('io.textile.dropzone')
    // console.log(buck.root, 'ROOT')
    if (!buck.root) {
      throw new Error('Failed to open bucket')
    }
    return {buckets: buckets, bucketKey: buck.root.key}
  }

  getClientInfo = async () => {
    if (!this.state.identity) {
      throw new Error('Identity not set')
    }
    const client = await Client.withKeyInfo(this.keyInfo)
    console.log(client.listDBs(), 'ADSAD')
    await client.getToken(this.state.identity)
    return client
  }

  // createThread = async () => {
  //   const threadId = ThreadID.fromRandom()
  //   console.log(threadId)
  //   return threadId
  // }

  getDatabase = async (client: Client) => {
    return client.newDB(undefined, 'nasa')
  }

  /**
   * getBucketLinks returns all the protocol endpoints for the bucket.
   * Read more:
   * https://docs.textile.io/hub/buckets/#bucket-protocols
   */
  getBucketLinks = async () => {
    if (!this.state.buckets || !this.state.bucketKey) {
      console.error('No bucket client or root key')
      return
    }
    const links = await this.state.buckets.links(this.state.bucketKey)
    // console.log(links, 'LOKN')
    this.setState({
      ...links
    })
  }

  render () {
    return (
      <div className="App">
        <Segment.Group style={{ height: "100%", border: 0, boxShadow: "0px 0px 0px", borderRadius: 0 }}>
          <Segment clearing className="nav">
            <Header className="avatar" as="h2" floated="left" title={this.state.identity ? this.state.identity.toString() : 'identity'}>
              {this.state.identity && <div className="avatar">|<span role="img">üç£</span>|<span role="img">üç£</span>|</div>}
            </Header>
            {this.state.url &&
              <a href={this.state.url} target="_blank" rel="noopener noreferrer">
                <Button
                  className="link"
                  floated="right"
                >BUCKET</Button>
              </a>
            }
            {this.state.www &&
              <a href={this.state.www} target="_blank" rel="noopener noreferrer">
                <Button
                  className="link"
                  floated="right"
                >WWW</Button>
              </a>
            }
            {this.state.ipns &&
              <a href={this.state.ipns} target="_blank" rel="noopener noreferrer">
                <Button
                  className="link"
                  floated="right"
                >IPNS</Button>
              </a>
            }
          </Segment>
          {this.state.threadId && this.state.client && (
            <Table
              threadId={this.state.threadId}
              client={this.state.client}
            />
          )}
        </Segment.Group>
      </div>
    )
  }
}

export default App
