import React, { useState, useEffect } from 'react'
import Table from './Table'

import "semantic-ui-css/semantic.min.css"
import { Buckets, KeyInfo, PrivateKey, Client, ThreadID } from '@textile/hub'
import { Button, Header, Segment } from "semantic-ui-react"
import './App.css'

const App = () => {
  const [keyInfo] = useState<KeyInfo>({
    key: 'bs3g66aciasarrm46kosxap74te',
  })
  const [identity, setIdentity] = useState<PrivateKey>()
  const [client, setClient] = useState<Client>()
  const [threadId, setThreadId] = useState<ThreadID>()
  const [links, setLinks] = useState<[]>()

  useEffect(() => {
    const setup = async () => {
      const newIdentity = await getIdentity()
      setIdentity(newIdentity)
      const {bucketKey, buckets} = await getBucketKey(newIdentity)
      await getBucketLinks(bucketKey, buckets)

      const client = await getClientInfo(newIdentity)
      const threadId = await ThreadID.fromRandom()
      await client.newDB(threadId)
      setClient(client)
      setThreadId(threadId)
    }
    setup()
  }, [])

  const getIdentity = async (): Promise<PrivateKey> => {
    try {
      var storedIdent = localStorage.getItem("identity")
      if (storedIdent === null) {
        throw new Error('No identity')
      }
      const restored = PrivateKey.fromString(storedIdent)
      return restored
    }
    catch (e) {
      try {
        const identity = await PrivateKey.fromRandom()
        const identityString = identity.toString()
        localStorage.setItem("identity", identityString)
        return identity
      } catch (err) {
        return err.message
      }
    }
  }

  const getBucketKey = async (identity: PrivateKey) => {
    if (!identity) {
      throw new Error('Identity not set')
    }
    const buckets = await Buckets.withKeyInfo(keyInfo)
    await buckets.getToken(identity)
    const buck = await buckets.getOrCreate('io.textile.dropzone')
    if (!buck.root) {
      throw new Error('Failed to open bucket')
    }
    return {buckets: buckets, bucketKey: buck.root.key}
  }

  const getClientInfo = async (identity: PrivateKey) => {
    if (!identity) {
      throw new Error('Identity not set')
    }
    const client = await Client.withKeyInfo(keyInfo)
    await client.getToken(identity)
    return client
  }

  const getBucketLinks = async (bucketKey: string, buckets: Buckets) => {
    if (!buckets || !bucketKey) {
      console.error('No bucket client or root key')
      return
    }
    const linksList = await buckets.links(bucketKey)
    console.log(linksList, 'LINKS')
    // setLinks(linksList)
  }

  return (
    <div className="App">
      <Segment.Group style={{ height: "100%", border: 0, boxShadow: "0px 0px 0px", borderRadius: 0 }}>
        <Segment clearing className="nav">
          <Header className="avatar" as="h2" floated="left" title={identity ? identity.toString() : 'identity'}>
            {identity && <div className="avatar">|<span role="img">üç£</span>|<span role="img">üç£</span>|</div>}
          </Header>
          { /* links.url &&
            <a href={links.url} target="_blank" rel="noopener noreferrer">
              <Button
                className="link"
                floated="right"
              >BUCKET</Button>
            </a>
          */ }
          { /* links.www &&
            <a href={links.www} target="_blank" rel="noopener noreferrer">
              <Button
                className="link"
                floated="right"
              >WWW</Button>
            </a>
          */ }
          { /* links.ipns &&
            <a href={links.ipns} target="_blank" rel="noopener noreferrer">
              <Button
                className="link"
                floated="right"
              >IPNS</Button>
            </a>
          */ }
        </Segment>
        {threadId && client && (
          <Table
            threadId={threadId}
            client={client}
          />
        )}
      </Segment.Group>
    </div>
  )
}

export default App
