import React, { useState, useEffect } from 'react'
import { connect } from 'redux-bundler-react'
import BaseScreen from './BaseScreen'
import Table from './table/Table'
import './ThreadScreen.css'


interface IThreadsScreen {
  authClient: any
  threadsActiveCollections: any
  doCollectionsCreate: (name: string) => any
  doThreadsSetActive: (threadId: string) => any
  routeParams: any
}

const ThreadScreen = ({
  authClient,
  threadsActiveCollections,
  doCollectionsCreate,
  doThreadsSetActive,
  routeParams
}: IThreadsScreen) => {
  const [name, setName] = useState<string>('')
  const threadId = routeParams.threadId

  useEffect(() => {
    const setup = async () => {
      if (threadId && authClient && !threadsActiveCollections) {
        doThreadsSetActive(threadId)
      }
      if (threadsActiveCollections) {
        setName(threadsActiveCollections[0].name)
      }
    }
    setup()
  }, [threadsActiveCollections, authClient])

  const selectExisting = (name: string) => {
    setName(name)
  }
  return (
    <BaseScreen>
      <div className='optionsRow'>
        {threadsActiveCollections && threadsActiveCollections.map((collection: any, index: number) => (
          <div
            key={index}
            className='tableButton'
            onClick={() => selectExisting(collection.name)}
          >
            {collection.name}
          </div>
        ))}
        <div className="inputContainer">
          <input className='nameInput' placeholder='table name' type='text' value={name} onChange={e => setName(e.target.value)}/>
          <a className='ui primary button' href={`/threads/${threadId}/${name}`}>Create</a>
        </div>
      </div>
      {threadsActiveCollections && (
        <Table name={name}/>
      )}
    </BaseScreen>
  )
}

export default connect(
  'doThreadsCreate',
  'doThreadsSetActive',
  'doCollectionsCreate',
  'selectAuthClient',
  'selectThreadsActiveCollections',
  'selectRouteParams',
  ThreadScreen
)
