import React, { useState, useEffect } from 'react'
import { connect } from 'redux-bundler-react'
import BaseScreen from './BaseScreen'
import { Button } from 'semantic-ui-react'
import Table from './table/Table'
import './ThreadScreen.css'


interface IThreadsScreen {
  authClient: any
  threadsActive: any
  doCollectionsCreate: (name: string) => any
  doThreadsSetActive: (threadId: string) => any
  routeParams: any
}

const ThreadScreen = ({
  authClient,
  threadsActive,
  doCollectionsCreate,
  doThreadsSetActive,
  routeParams
}: IThreadsScreen) => {
  const [name, setName] = useState<string>('')

  useEffect(() => {
    const setup = async () => {
      const threadId = routeParams.threadId
      if (threadId && authClient) {
        doThreadsSetActive(threadId)
      }
      if (threadsActive) {
        setName(threadsActive[0].name)
      }
    }
    setup()
  }, [threadsActive, authClient])

  const selectExisting = (name: string) => {
    setName(name)
  }
  return (
    <BaseScreen>
      <div className='optionsRow'>
        {threadsActive && threadsActive.map((collection: any, index: number) => (
          <div
            key={index}
            className='tableButton'
            onClick={() => selectExisting(collection.name)}
          >
            {collection.name}
          </div>
        ))}
        <div className="inputContainer">
          <input className='nameInput' placeholder='table name' type='text' value={name} onChange={e => setName(e.target.value)}/>
          <Button className='ui primary button' onClick={() => doCollectionsCreate(name)}>Create</Button>
        </div>
      </div>
      {threadsActive && (
        <Table name={name}/>
      )}
    </BaseScreen>
  )
}

export default connect(
  'doThreadsCreate',
  'doThreadsSetActive',
  'doCollectionsCreate',
  'selectAuthClient',
  'selectThreadsActive',
  'selectRouteParams',
  ThreadScreen
)
